import React, { useEffect, useRef } from "react";
import { motion, useInView } from "framer-motion";

interface MarqueeProps {
  children: React.ReactNode;
  speed?: number;
  direction?: "left" | "right";
  pauseOnHover?: boolean;
}

const Marquee: React.FC<MarqueeProps> = ({
  children,
  speed = 10,
  direction = "left",
  pauseOnHover = true,
}) => {
  const ref = useRef<HTMLDivElement>(null);
  const isInView = useInView(ref);

  useEffect(() => {
    const marquee = ref.current;
    if (!marquee) return;

    const scrollAnimation = () => {
      const scrollAmount = direction === "left" ? -speed : speed;
      marquee.scrollLeft += scrollAmount;
      if (marquee.scrollLeft >= marquee.scrollWidth - marquee.clientWidth) {
        marquee.scrollLeft = 0;
      }
    };

    const animationFrame = () => {
      if (isInView) {
        scrollAnimation();
      }
      requestAnimationFrame(animationFrame);
    };

    animationFrame();

    if (pauseOnHover) {
      marquee.addEventListener("mouseover", () => {
        requestAnimationFrame(() => {
          marquee.style.animationPlayState = "paused";
        });
      });
      marquee.addEventListener("mouseout", () => {
        requestAnimationFrame(() => {
          marquee.style.animationPlayState = "running";
        });
      });
    }
  }, [speed, direction, pauseOnHover, isInView]);

  return (
    <div
      ref={ref}
      className="relative overflow-hidden"
      style={{ width: "100%", height: "100%" }}
    >
      <motion.div
        ref={ref}
        className="absolute top-0 left-0 flex items-center"
        animate={{ x: direction === "left" ? "-100%" : "100%" }}
        transition={{
          duration: 100000,
          ease: "linear",
          repeat: Infinity,
        }}
      >
        {children}
      </motion.div>
    </div>
  );
};

export default Marquee;
